set.seed(42)
n <- 100  # número de simulaciones
k <- 10  # número de personas
count <- 0
for (i in 1:n) {
  x <- sample(365, k, replace = T)
  coinciden <- any(table(x) > 1)
  if (coinciden) {
    count <- count + 1
  }
}
count / n## [1] 0.13

n <- 10
i <- 3
termina <- replicate(1000, {
  while (!(i %in% c(0, n))) {
    paso <- 2 * sample(2, 1) - 3
    i <- i + paso
  }
  i
})
table(termina)## termina
